SELECT fbi.id AS inst_id, fbi.apply_id as inst_apply_id, fbi.apply_name as inst_apply_name, fbi.org_id as inst_org_id, fbi.org_name as inst_org_name, fbi.fb_title as inst_fb_title, fbi.fb_content as inst_fb_content, fbi.start_date_time as inst_start_date_time, fbi.inst_status as inst_status, fbt.id AS inst_fb_id, fbt.t_name as inst_t_name, fbt.t_code as inst_t_code, fbt.project_flag as inst_project_flag, fbt.handle_flag as inst_handle_flag, fbt1.id AS inst_t_type_id, fbt1.t_name AS inst_t_type_name, fbt2.id AS inst_parent_t_type_id, fbt2.t_name AS inst_parent_t_type_name , tk.id_ as tk_task_id, tk.proc_inst_id_ as tk_proc_inst_id, tk.task_def_key_ as tk_cur_tag_id, tk.name_ as tk_cur_tag_name, ( CASE when (tk.assignee_ IS not null) then tk.assignee_ when (tk.assignee_ is null and tk.user_id_ is not null ) then tk.user_id_ when (tk.assignee_ IS NULL and tk.user_id_ IS NULL) then tk.emp_id END ) AS tk_cur_tag_assignee FROM ( select distinct fbi.* from fb_bus_inst fbi JOIN fb_bus_template fbt ON fbi.fb_id = fbt.id JOIN fb_bus_type fbt1 ON fbt.f_b_t_id = fbt1.id JOIN fb_bus_type fbt2 ON fbt.parent_id = fbt2.id left join fb_bus_project fbpt on fbpt.inst_id=fbi.id where fbi.inst_status!='0'
and fbt.t_code=#{t_code}
and fbt.t_name like CONCAT('%',#{t_name},'%')
and fbt1.t_name like CONCAT('%',#{t_type_name},'%')
and fbt2.t_name like CONCAT('%',#{parent_t_type_name},'%')
and fbi.apply_name=#{apply_name}
and fbi.org_name like CONCAT('%',#{org_name},'%')
and fbi.inst_status =#{inst_status}
and fbi.fb_title like CONCAT('%',#{fb_title},'%')
and fbi.fb_content like CONCAT('%',#{fb_content},'%')
AND fbi.start_date_time >= #{begin_start_date_time}
AND fbi.start_date_time <= #{end_start_date_time}
and fbi.apply_id =#{apply_id}
and (fbi.fb_title like CONCAT('%',#{search},'%') or fbi.id like CONCAT('%',#{search},'%'))
and fbi.id =#{inst_id}
and fbpt.project_code =#{project_code}
and fbpt.project_name =#{project_name}
order by fbi.start_date_time desc
limit #{firstresult},#{maxResult} ) fbi JOIN fb_bus_template fbt ON fbi.fb_id = fbt.id JOIN fb_bus_type fbt1 ON fbt.f_b_t_id = fbt1.id JOIN fb_bus_type fbt2 ON fbt.parent_id = fbt2.id left join act_hi_procinst are on are.business_key_=fbi.id left join ( SELECT DISTINCT RES.*,fburole.emp_id ,i.user_id_ FROM ACT_ru_TASK RES LEFT JOIN ACT_ru_IDENTITYLINK I ON I.TASK_ID_ = RES.ID_ LEFT JOIN fb_bus_role fbrole ON fbrole.role_code = i.group_id_ LEFT JOIN fb_bus_user_role fburole ON fburole.role_id = fbrole.role_id where 1=1
and RES.ASSIGNEE_ = #{cur_tag_assignee} OR (RES.ASSIGNEE_ IS NULL AND I.TYPE_ = 'candidate' AND I.USER_ID_ = #{cur_tag_assignee} ) or (RES.ASSIGNEE_ IS NULL AND i.group_id_ IS NOT NULL AND I.TYPE_ = 'candidate' AND fburole.emp_id = #{cur_tag_assignee})
) tk on tk.proc_inst_id_=are.proc_inst_id_

