mapper.select:
SELECT fbi.id AS inst_id, fbi.apply_id as inst_apply_id, fbi.apply_name as inst_apply_name, fbi.org_id as inst_org_id, fbi.org_name as inst_org_name, fbi.fb_title as inst_fb_title, fbi.fb_content as inst_fb_content, fbi.start_date_time as inst_start_date_time, fbi.inst_status as inst_status, fbt.id AS inst_fb_id, fbt.t_name as inst_t_name, fbt.t_code as inst_t_code, fbt.project_flag as inst_project_flag, fbt.handle_flag as inst_handle_flag, fbt1.id AS inst_t_type_id, fbt1.t_name AS inst_t_type_name, fbt2.id AS inst_parent_t_type_id, fbt2.t_name AS inst_parent_t_type_name FROM fb_bus_care fbc join fb_bus_inst fbi on fbc.inst_id=fbi.id JOIN fb_bus_template fbt ON fbi.fb_id = fbt.id JOIN fb_bus_type fbt1 ON fbt.f_b_t_id = fbt1.id JOIN fb_bus_type fbt2 ON fbt.parent_id = fbt2.id left join fb_bus_project fbp on fbp.inst_id=fbi.id
where fbi.inst_status !='0'
and fbt.t_code=#{t_code}
and fbt.t_name like CONCAT(#{t_name},'%')
and fbt1.t_name like CONCAT(#{t_type_name},'%')
and fbt2.t_name like CONCAT(#{parent_t_type_name},'%')
and fbi.apply_name=#{apply_name}
and fbi.org_name like CONCAT(#{org_name},'%')
and fbi.inst_status =#{inst_status}
and fbi.fb_title like CONCAT('%',#{fb_title},'%')
and fbi.fb_content like CONCAT('%',#{fb_content},'%')
AND fbi.start_date_time >= #{begin_start_date_time}
AND fbi.start_date_time <= #{end_start_date_time}
and fbi.apply_id =#{apply_id}
and (fbi.fb_title like CONCAT('%',#{search},'%') or fbi.id like CONCAT('%',#{search},'%'))
and fbi.id =#{inst_id}
and fbp.project_name =#{project_name}
and fbp.project_code =#{project_code}
and fbc.emp_id=#{care_emp_id}
order by fbc.create_date_time desc

SELECT fbc.id, fbc.inst_id, t.t_code, i.fb_title, i.org_id, i.org_name, i.apply_id, i.apply_name, bt.id AS t_type_id, bt.t_name AS t_type_name, i.start_date_time, i.inst_status FROM fb_bus_care fbc, fb_bus_inst i, fb_bus_template t, fb_bus_type bt WHERE fbc.inst_id = i.id AND i.fb_id = t.id AND t.f_b_t_id = bt.id AND bt.t_enabled = 1
AND (i.fb_title LIKE CONCAT ('%',#{search},'%') OR t.t_code LIKE CONCAT ('%',#{search},'%'))
AND i.apply_name LIKE CONCAT (#{apply_name},'%')
AND i.org_name LIKE CONCAT ('%',#{org_name},'%')
AND bt.t_name LIKE CONCAT('%',#{t_type_name},'%')
AND i.start_date_time >= #{begin_start_date_time}
AND i.start_date_time <= #{end_start_date_time}
AND i.inst_status = #{inst_status}
AND fbc.emp_id = #{emp_id}
and (i.title like CONCAT('%',#{search},'%') or i.inst_id like CONCAT('%',#{search},'%'))
and i.id =#{inst_id}
order by fbc.create_date_time desc
